mapper.select:
select id, fid, name, content, details, create_time, 0 as chartType from tb_chart where del_flag = 1 and create_user = #{query.id}
and name like concat('%', #{query.name}, '%')
and details like concat('%', #{query.details}, '%')
and (name like concat('%', #{query.content}, '%') or details like concat('%', #{query.content}, '%'))
and fid = #{query.folderId}
union all select id, fid, name, content, details, create_time, 1 as chartType from tb_draft_chart where del_flag = 1 and create_user = #{query.id}
and name like concat('%', #{query.name}, '%')
and details like concat('%', #{query.details}, '%')
and (name like concat('%', #{query.content}, '%') or details like concat('%', #{query.content}, '%'))
and fid = #{query.folderId}
