mapper.select:
select id, name, content, details, create_time, 0 as chartType from tb_chart where del_flag = 1 and create_user = #{query.id}
and name like concat('%', #{query.name}, '%')
and details like concat('%', #{query.details}, '%')
union all select id, name, content, details, create_time, 1 as chartType from tb_draft_chart where del_flag = 1 and create_user = #{query.id}
and name like concat('%', #{query.name}, '%')
and details like concat('%', #{query.details}, '%')
