select api.id, api_name, api_code, api_desc, api_type, api.datasource_id, source.datasource_type, table_name, table_name_alias, table_type, table_business_type, create_sql, create_count_sql, api_proxy_url, create_api_type, api.create_time, api.create_user, api.important_interface, api.expiration_time, api.expiration_type, api.menu_id, api.api_menu_id, api.enable_cache, api.cache_time -- userinfo.user_account as create_user from tb_api_config as api left join tb_datasource_config as source ON api.datasource_id = source.id where api.del_flag=1 and api.menu_id = #{query.menuId}
and api.create_api_type = #{query.createApiType}
and (api_name like concat('%', #{query.keyword}, '%') or api_desc like concat('%', #{query.keyword}, '%') or api_code like concat('%', #{query.keyword}, '%'))
and EXISTS(select field.* FROM tb_field_config field WHERE field.api_id = api.id AND field.del_flag = 1
and (field.field_name like concat('%', #{query.keyword}, '%') or field.field_desc like concat('%', #{query.keyword}, '%')) )

SELECT api.id, api.api_code, api.api_name, api.api_desc, api.api_type, api.datasource_id, api.create_sql, api.create_count_sql, api.important_interface, api.expiration_time, api.expiration_type FROM tb_api_config api LEFT JOIN tb_app_service_config appapi ON api.id = appapi.service_id WHERE api.del_flag = 1 AND appapi.del_flag = 1
AND appapi.app_id = #{appId}
AND api.id IN
#{item}
ORDER BY appapi.create_time DESC
