mapper.select:
SELECT *
FROM tb_metadata_entity
WHERE type_id = #{type}
  and parent_id = #{entityId}
  and del_flag=1
ORDER BY name

SELECT tb_metadata_entity.id id, tb_metadata_entity.name name, tb_metadata_entity.display_name dname, tb_business_classification.`name` cname, tb_business_classification.`pid` pid FROM tb_metadata_entity LEFT JOIN tb_metadata_classification_map ON tb_metadata_entity.id = tb_metadata_classification_map.metadata_entity_id LEFT JOIN tb_business_classification ON tb_metadata_classification_map.business_classification_id = tb_business_classification.id WHERE tb_metadata_entity.id IN
#{id}
and tb_metadata_entity.del_flag = 1 and tb_business_classification.del_flag = 1 and tb_metadata_classification_map.del_flag = 1
