SELECT log.id, log.app_id, log.api_id, log.log_type, log.log_level, log.log_request, log.log_response_info, log.log_info, log.business_state, log.create_time AS create_time, api.api_name, api.api_code, app.app_name, app.app_account FROM tb_logs AS log LEFT JOIN tb_app_config AS app ON log.app_id = app.id LEFT JOIN tb_api_config AS api ON log.api_id = api.id WHERE log.del_flag = 1
AND log.api_id IN
#{item}
AND log.app_id = #{appId}
