mapper.select:
SELECT app.id AS appId, app.app_type AS appType, api.id AS apiId, api.api_name AS apiName FROM tb_app_registration AS app LEFT JOIN tb_api_config AS api ON api.app_id = app.id WHERE app.del_flag = 1 AND api.del_flag = 1 AND api.publish = 1
AND app.id = #{appId}
AND api.id = #{apiId}

SELECT app.id AS appId, app.app_type AS appType, api.id AS apiId, api.api_name AS apiName FROM tb_app_registration AS app LEFT JOIN tb_api_config AS api ON api.app_id = app.id WHERE app.del_flag = 1 AND api.del_flag = 1 AND api.publish=1
AND app.id = #{appId}
AND api.id = #{apiId}

SELECT app.id AS appId, app.app_type AS appType, tb.id AS tableId, tb.table_name AS tableName FROM tb_app_registration AS app LEFT JOIN tb_table_access AS tb ON tb.app_id = app.id WHERE app.del_flag = 1 AND tb.del_flag = 1 AND tb.publish = 1
AND app.id = #{appId}
AND tb.id = #{apiId}
