mapper.select:
SELECT * FROM (SELECT business_id, id, dimension_tab_name AS ods_table_name, Concat('stg_',dimension_tab_name) AS stg_table_name, 1 AS table_type FROM tb_dimension
UNION ALL
SELECT business_id, id, fact_tab_name AS ods_table_name, Concat('stg_',fact_tab_name) AS stg_table_name, 2 AS table_type FROM tb_fact) AS tab WHERE ods_table_name = #{tableName} OR stg_table_name = #{tableName}
