mapper.select:
SELECT log.id, log.id_uuid, log.rule_id, log.rule_name, log.template_id, log.fi_datasource_id, log.log_type, log.schema_name, log.table_name, log.field_name, log.check_template_name, log.check_batch_number, log.check_small_batch_number, log.check_total_count, log.check_fail_count, log.check_result, log.check_msg, log.check_rule_illustrate, log.check_data_accuracy, log.check_data_start_time, log.check_data_end_time, log.check_data_duration, log.check_data_sql, log.check_data_count_sql, log.create_time FROM tb_datacheck_rule_logs log WHERE log.del_flag =1
and log.log_type = #{query.logType}
and log.rule_id = #{query.ruleId}
and log.template_id = #{query.templateId}
and log.rule_name like concat('%', #{query.ruleName}, '%')
