mapper.select:
SELECT id, report_id, report_name, report_type, report_type_name, report_desc, report_principal, report_notice_type, email_server_id, subject, body, recipient, send_time, send_result, report_quality_grade, report_batch_number, report_rule_check_count, report_rule_check_error_count, report_rule_check_accuracy, report_rule_check_result, report_rule_check_epilogue, create_report_start_time, create_report_end_time, create_report_duration FROM tb_quality_report_log WHERE del_flag = 1 AND report_id=#{reportId}
AND report_id = #{reportId}
AND (report_name LIKE concat('%', #{keyword}, '%') OR send_time LIKE concat('%', #{keyword}, '%') OR send_result LIKE concat('%', #{keyword}, '%'))
AND report_batch_number = #{reportBatchNumber}
AND create_report_start_time >= #{createReportStartTime}
AND create_report_end_time <= #{createReportEndTime}
ORDER BY IFNULL(create_report_start_time,create_time) DESC
