mapper.select:
SELECT a.id as apply_id, a.approval_code, a.applicant, a.state, a.description, a.operation_type, a.application_time FROM tb_process_apply a WHERE a.applicant = #{userId} and a.del_flag = 1
and a.description like concat('%', #{query.keyword}, '%')
and a.state = #{query.state}
and a.application_time > #{query.startTime}
and a.application_time < #{query.endTime}

SELECT a.id as apply_id, a.approval_code, b.process_id, a.applicant, a.description, a.operation_type, a.application_time, a.approver_node FROM ( SELECT a.id, a.approval_code, a.applicant, a.description, a.operation_type, a.opreationstate, a.application_time, a.approver_node FROM tb_process_apply a WHERE a.del_flag = 1 ) a LEFT JOIN tb_process_node b ON a.approver_node = b.id LEFT JOIN tb_process_person c ON b.id = c.rocess_node_id WHERE a.opreationstate = 3 and ((c.type = 2 and c.urid = #{userId}) or (c.type = 1 and
c.urid = #{item}
))
and (
#{item}
a.description like concat('%', #{query.keyword}, '%') )
and a.operation_type = #{query.operationType}
and a.application_time > #{query.startTime}
and a.application_time < #{query.endTime}
GROUP BY a.id

SELECT a.id as apply_id, a.approval_code, a.process_id, a.applicant, a.description, a.operation_type, b.state, b.create_time as approval_time, a.application_time, a.approver_node FROM ( SELECT a.id, a.approval_code, a.process_id, a.applicant, a.description, a.operation_type, a.application_time, a.approver_node FROM tb_process_apply a WHERE a.del_flag = 1 ) a LEFT JOIN tb_process_apply_notes b ON a.id = b.processapply_id WHERE b.create_user = #{userId}
and (
#{item}
a.description like concat('%', #{query.keyword}, '%') )
and b.state = #{query.state}
and a.application_time > #{query.startTime}
and a.application_time < #{query.endTime}

SELECT a.id as apply_id, a.approval_code, a.process_id, a.applicant, a.description, a.operation_type, a.opreationstate, a.application_time, a.approver_node FROM tb_process_apply a WHERE a.del_flag = 1
and (
#{item}
a.description like concat('%', #{query.keyword}, '%') )
and a.opreationstate = #{query.opreationstate}
and a.application_time > #{query.startTime}
and a.application_time < #{query.endTime}
